import { Dumbbell, Plus } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import { useState } from "react";
import { AddWorkoutModal } from "../modals/add-workout-modal";
import type { Workout } from "@shared/schema";

export function FitnessTrackerWidget() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  const { data: workouts = [], isLoading } = useQuery<Workout[]>({
    queryKey: ["/api/workouts"],
  });

  // Calculate fitness stats
  const today = new Date().toISOString().split('T')[0];
  const todayWorkouts = workouts.filter(w => w.workoutDate === today);
  const totalSteps = 8347; // Mock data - would come from fitness API
  const totalCalories = todayWorkouts.reduce((sum, w) => sum + (w.calories || 0), 0) + 420; // Base calories

  // Weekly workout goal
  const thisWeek = new Date();
  const startOfWeek = new Date(thisWeek.setDate(thisWeek.getDate() - thisWeek.getDay() + 1));
  const weekWorkouts = workouts.filter(w => new Date(w.workoutDate) >= startOfWeek);
  const weeklyGoal = 5;
  const weeklyProgress = (weekWorkouts.length / weeklyGoal) * 100;

  return (
    <>
      <Card className="widget-card rounded-2xl p-6 animate-fade-in" data-testid="widget-fitness-tracker">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <Dumbbell className="text-dashboard-red mr-2" size={20} />
            Fitness
          </h3>
          <Button
            variant="ghost"
            size="icon"
            className="text-dashboard-red hover:text-red-300"
            onClick={() => setIsAddModalOpen(true)}
            data-testid="button-add-workout"
          >
            <Plus size={18} />
          </Button>
        </div>

        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="text-center">
              <p className="text-lg font-bold text-dashboard-red" data-testid="text-steps-today">
                {totalSteps.toLocaleString()}
              </p>
              <p className="text-xs text-gray-400">Steps today</p>
            </div>
            <div className="text-center">
              <p className="text-lg font-bold text-dashboard-orange" data-testid="text-calories-burned">
                {totalCalories}
              </p>
              <p className="text-xs text-gray-400">Calories burned</p>
            </div>
          </div>

          <div className="space-y-3">
            {isLoading ? (
              <div className="text-center text-gray-400 py-2">Loading workouts...</div>
            ) : workouts.slice(0, 2).map((workout) => (
              <div 
                key={workout.id}
                className="bg-white/5 rounded-lg p-3"
                data-testid={`workout-${workout.id}`}
              >
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm text-white font-medium">{workout.type}</span>
                  <span className="text-xs text-gray-400">
                    {new Date(workout.workoutDate).toLocaleDateString() === today ? 'Today' : 
                     new Date(workout.workoutDate).toLocaleDateString()}
                  </span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-xs text-gray-400">{workout.duration} min</span>
                  <span className="text-xs text-dashboard-red">{workout.calories || 0} cal</span>
                </div>
              </div>
            ))}

            {workouts.length === 0 && !isLoading && (
              <div className="text-center text-gray-400 py-2 text-sm">
                No workouts logged yet
              </div>
            )}
          </div>

          <div className="bg-white/5 rounded-lg p-3">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm text-white">Weekly Goal</span>
              <span className="text-sm text-gray-400" data-testid="text-weekly-progress">
                {weekWorkouts.length}/{weeklyGoal} workouts
              </span>
            </div>
            <div className="w-full bg-gray-700 rounded-full h-2">
              <div 
                className="bg-gradient-to-r from-dashboard-red to-pink-400 h-2 rounded-full transition-all duration-300"
                style={{ width: `${Math.min(weeklyProgress, 100)}%` }}
              />
            </div>
          </div>
        </div>
      </Card>

      <AddWorkoutModal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
      />
    </>
  );
}

import { CheckCircle, Plus } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useState } from "react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { AddHabitModal } from "../modals/add-habit-modal";
import type { Habit } from "@shared/schema";

export function HabitTrackerWidget() {
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  const { data: habits = [], isLoading } = useQuery<Habit[]>({
    queryKey: ["/api/habits"],
  });

  const updateHabitMutation = useMutation({
    mutationFn: async ({ id, weekProgress }: { id: string; weekProgress: boolean[] }) => {
      await apiRequest("PATCH", `/api/habits/${id}`, { weekProgress });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/habits"] });
    },
  });

  const toggleHabitDay = (habitId: string, dayIndex: number, currentProgress: boolean[]) => {
    const newProgress = [...currentProgress];
    newProgress[dayIndex] = !newProgress[dayIndex];
    updateHabitMutation.mutate({ id: habitId, weekProgress: newProgress });
  };

  const getDotClass = (completed: boolean, dayIndex: number) => {
    const today = new Date().getDay(); // 0 = Sunday, 1 = Monday, etc.
    const adjustedToday = today === 0 ? 6 : today - 1; // Convert to 0-6 where 0 = Monday

    if (dayIndex < adjustedToday) {
      return completed ? 'habit-dot completed' : 'habit-dot missed';
    } else if (dayIndex === adjustedToday) {
      return 'habit-dot pending';
    } else {
      return 'habit-dot pending';
    }
  };

  return (
    <>
      <Card className="widget-card rounded-2xl p-6 animate-fade-in" data-testid="widget-habit-tracker">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-white flex items-center">
            <CheckCircle className="text-dashboard-cyan mr-2" size={20} />
            Habits
          </h3>
          <Button
            variant="ghost"
            size="icon"
            className="text-dashboard-cyan hover:text-cyan-300"
            onClick={() => setIsAddModalOpen(true)}
            data-testid="button-add-habit"
          >
            <Plus size={18} />
          </Button>
        </div>

        <div className="space-y-4">
          {isLoading ? (
            <div className="text-center text-gray-400 py-4">Loading...</div>
          ) : habits.length === 0 ? (
            <div className="text-center text-gray-400 py-4">
              No habits yet. Add your first habit!
            </div>
          ) : (
            habits.map((habit) => (
              <div 
                key={habit.id}
                className="bg-white/5 rounded-lg p-3"
                data-testid={`habit-${habit.id}`}
              >
                <div className="flex items-center justify-between mb-3">
                  <span className="text-sm text-white font-medium">{habit.name}</span>
                  <span className="text-xs text-dashboard-cyan">
                    {habit.streak || 0} day streak
                  </span>
                </div>
                <div className="flex items-center space-x-1">
                  {(habit.weekProgress || [false, false, false, false, false, false, false]).map((completed, dayIndex) => (
                    <button
                      key={dayIndex}
                      className={getDotClass(completed, dayIndex)}
                      onClick={() => toggleHabitDay(habit.id, dayIndex, habit.weekProgress || [false, false, false, false, false, false, false])}
                      title={`${['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'][dayIndex]}: ${completed ? 'Completed' : 'Not completed'}`}
                      data-testid={`habit-dot-${habit.id}-${dayIndex}`}
                    />
                  ))}
                </div>
              </div>
            ))
          )}
        </div>
      </Card>

      <AddHabitModal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
      />
    </>
  );
}

import { Utensils, Plus } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import type { Meal } from "@shared/schema";

export function MealPlanningWidget() {
  const { data: meals = [], isLoading } = useQuery<Meal[]>({
    queryKey: ["/api/meals"],
  });

  const weekDays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  // Group meals by day of week and meal type
  const mealsByDay = weekDays.map((day, dayIndex) => {
    const dayMeals = meals.filter(meal => meal.dayOfWeek === dayIndex + 1);
    return {
      day,
      breakfast: dayMeals.find(m => m.mealType === 'breakfast')?.name || '',
      lunch: dayMeals.find(m => m.mealType === 'lunch')?.name || '',
      dinner: dayMeals.find(m => m.mealType === 'dinner')?.name || ''
    };
  });

  return (
    <Card className="widget-card rounded-2xl p-6 animate-fade-in" data-testid="widget-meal-planning">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-white flex items-center">
          <Utensils className="text-dashboard-orange mr-2" size={20} />
          Meal Planning
        </h3>
        <Button
          variant="ghost"
          size="icon"
          className="text-dashboard-orange hover:text-orange-300"
          data-testid="button-add-meal"
        >
          <Plus size={18} />
        </Button>
      </div>

      <div className="grid grid-cols-7 gap-2">
        {mealsByDay.map((dayData, index) => (
          <div key={index} className="text-center" data-testid={`meal-day-${index}`}>
            <p className="text-xs text-gray-400 mb-2">{dayData.day}</p>
            <div className="bg-white/5 rounded-lg p-2 min-h-16 hover:bg-white/10 transition-colors cursor-pointer">
              {dayData.breakfast && (
                <p className="text-xs text-white font-medium mb-1 truncate" title={dayData.breakfast}>
                  {dayData.breakfast}
                </p>
              )}
              {dayData.lunch && (
                <p className="text-xs text-gray-400 truncate" title={dayData.lunch}>
                  {dayData.lunch}
                </p>
              )}
              {dayData.dinner && (
                <p className="text-xs text-gray-500 truncate" title={dayData.dinner}>
                  {dayData.dinner}
                </p>
              )}
              {!dayData.breakfast && !dayData.lunch && !dayData.dinner && (
                <div className="flex items-center justify-center h-full">
                  <Plus size={12} className="text-gray-600" />
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </Card>
  );
}

